<script>

    $(document).ready(function () {
        $('.nav-button').click(function () {
            $('body').toggleClass('nav-open');
        });
    });


    $(function () {

        $('.showSingle').click(function () {
            $('.targetDiv').hide();
            $('.showSingle').removeClass('')
            $('.showSingle').removeClass('active_product')
            $('#div' + $(this).addClass('active_product').attr('target')).show()
        });
    });

    function addCart(id,showCart=null,withCount=null)
    {
        var option = null;
        let quantity = null;
        if($('#input_option').length > 0) {
            option = $('#input_option').val();
        }
        if(withCount == 'withCount') {
            if($('#itemQuantitiy').length > 0) {
                quantity = $('#itemQuantitiy').val();
            }
        }
        $.ajax({
            url: "{{route('cart.send')}}",
            type: "POST",
            dataType: 'json',
            headers: {
                'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
            },
            data: {id: id,option:option,quantity:quantity},
            success: function (item) {
               if(item.success == 1){
                   toastr.success('{{__('messages.cart_add')}}', { timeOut: 1000 });

                   $('.item_count').css('display','block')
                   $('.item_count').html(item.data)
                   if(showCart != 1) {
                        ajaxCartItems()
                    }
               }
               if(item.success == 0) {
                    toastr.error('{{__('messages.product_is_out_of_stock')}}', { timeOut: 1000 });
               }
            }
        })
    }
