       <script>
           var isCartDialogOpen = false;
           $('.cart_popup,#continiue').on('click',function(e){
               e.preventDefault();
               if(isCartDialogOpen == false) {
                    isCartDialogOpen = true;
                    ajaxCartItems()
                }else {
                    isCartDialogOpen = false;
                    $('.quick_cart').animate({ opacity: 0, top: "0" }, 'fast',function() {
                        $('.quick_cart').css('display', 'none');
                        $(this).removeClass('quick_cart_show')
                        });
                    //
                }
           })
           $(document).on('click', function (event) {
            if (!$(event.target).closest('.quick_cart').length) {
                let element = $('.quick_cart');
                if(element.hasClass('quick_cart_show')) {
                    isCartDialogOpen = false;
                    element.animate({ opacity: 0, top: "0" }, 'fast',function() {
                    $('.quick_cart').css('display', 'none');
                    $(this).removeClass('quick_cart_show')
                });
            }
            }
            });
           $('body').on('click',':not(.quick_cart, .another-div-class, .parent-div-class *)',function(e) {

           })
           function ajaxCartItems(){
            $.ajax({
                    url: "/get-cart-ajax",
                    type: "GET",
                    dataType: 'json',
                    headers: {
                        'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
                    },
                    success: function (response) {
                        $('.quick_cart').css('display', 'block'); //if you have display: none on the element
                        $('.quick_cart').animate({ opacity: 1, top: "50px" }, 'fast',function(){
                            $(this).addClass('quick_cart_show')
                        });
                        //$('.quick_cart').slideDown(100);
                        let showCount = $('#widget_count');
                        if(showCount.length > 0) {
                            showCount.text(response.count)
                        }
                        $('#show_cart_widget').html(response.cart);
                    }
                })
           }
           function getPromotions() {
               //get-promotions-list

               $.ajax({
                    url: "/get-promotions-list",
                    type: "GET",
                    dataType: 'html',
                    headers: {
                        'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
                    },
                    success: function (response) {
                        if($('#show_promotions').length > 0) {
                            $('#show_promotions').html(response);
                        }
                    }
                })
           }
       </script>
           <script>
                getPromotions();
            function cartManipulate(id,type) {
                $.ajax({
                    url: "{{route('cart.cart.manipulate')}}",
                    type: "POST",
                    dataType: 'json',
                    headers: {
                        'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
                    },
                    data: {id: id,type: type},
                    success: function (item) {
                        $('#cartItems').html(item.cart);
                        getPromotions();
                        if(item.cart_count > 0){
                            $('.item_count').html(item.cart_count)
                        }
                        else{
                            $('.item_count').hide()
                        }
                        let cart_page = $('#cart_page');
                        if(cart_page.length == 0) {
                            ajaxCartItems();
                        }

                    }
                })
            }

    function toggleWishlist(id) {
       let className = $('[data-wishlist-btn-id="'+id+'"] > i').attr('class');
       if(className == 'dn-heart') {
        removeWishlist(id)
        $('[data-wishlist-btn-id="'+id+'"] > i').attr('class','dn-heart-o')
       }
       if(className == 'dn-heart-o') {
           addWishlist(id)
           $('[data-wishlist-btn-id="'+id+'"] > i').attr('class','dn-heart')
        }
    }
    function addWishlist(id)
    {
        $.ajax({
            url: "{{route('wishlist.add')}}",
            type: "POST",
            dataType: 'json',
            headers: {
                'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
            },
            data: {id: id},
            success: function (item) {
               if(item.success == 1){
                   toastr.success('{{__('messages.wishlist_add')}}', { timeOut: 1000 });
               }
            }
        })
    }
    function removeWishlist(id)
    {
        $.ajax({
            url: "{{route('wishlist.remove')}}",
            type: "POST",
            dataType: 'json',
            headers: {
                'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
            },
            data: {id: id},
            success: function (item) {
               if(item.success == 1){
                   toastr.success('{{__('messages.wishlist_remove')}}', { timeOut: 1000 });
                   $('[data-wishlist-id="'+id+'"]').animate({ opacity: 0, top: "0" }, 'slow',function() {
                        $('[data-wishlist-id="'+id+'"]').remove()
                        });
               }
            }
        })
    }
        </script>
